package com.javalec.function;


public class SumEvenOdd {

	// Field , Property
	
	
	// Constructor
	public SumEvenOdd() {													//Sum 컨트롤스페이스
		// TODO Auto-generated constructor stub
	}
	
	// Method
	// 합계를 구한다.
	public int sumCalc(int startNum, int endNum) {						// 정수 2개 입력하면 정수인트타입으로 리턴~주는 메소드/ 메소드는 소문자로시작
		int sum = 0;
		for(int i=startNum; i<=endNum; i++) {
			sum += i;
		}
		return sum;
	}
	
	
	// 짝수 홀수 판단.
	
	
	
	
	
	
}
