package com.javalec.addsub;

public class Sub {


	
	int startNum;
	int endNum;
	int sum;
	
	
	public Sub() {
		// TODO Auto-generated constructor stub
	}


	public Sub(int startNum, int endNum) {
		super();
		this.startNum = startNum;
		this.endNum = endNum;
	}
	
	public int Sub() {
		// TODO Auto-generated method stub
		sum = startNum+endNum;
		return sum;
	}
	
	
	
}
